import React from 'react';
import classes from './CardList.css'

import Moment from 'moment';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Checkbox from '@material-ui/core/Checkbox';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Typography from '@material-ui/core/Typography';

const CardList = (props) => {

    let assigneeList

    let cardData = props.info.map((el, index) => {
        return (
                <li className={classes.CardContainer}>
                    <Card className={classes.Card}>
                        <ExpansionPanel>
                            <ExpansionPanelSummary 
                                className={classes.CardSummary}
                                expandIcon={<ExpandMoreIcon />}>
                                <label>
                                    {el.text}
                                </label>
                            </ExpansionPanelSummary>
                            <ExpansionPanelDetails>
                                <Typography variant="subtitle1">
                                    <div className={classes.ExpandCreatorBox}>
                                        <p>Task created by: {el.creator.name}</p>
                                        <img src={`${props.imgPrefix}${el.creator.image}`} alt="Creator"/>
                                    </div>
                                    <div className={classes.ExpandInfoBox}>
                                        <p>
                                            Due: {Moment(el.startDate).format('MMMM Do YYYY')}
                                        </p>
                                        <label>Assignees</label>
                                        <ul>
                                            <li>
                                                {el.assignees.map(el => {
                                                    return <p>{el.full_name}</p>
                                                })}
                                            </li>
                                        </ul>
                                    </div>
                                </Typography>
                            </ExpansionPanelDetails>
                        </ExpansionPanel>
                    </Card>
                    <Checkbox
                        className={classes.Checkbox}
                        color="primary"
                        onClick={() => props.boxClick(index)}
                    />
                </li>
            )
        })

    return (
        <ul>
            {cardData}
        </ul>
    )
}



export default CardList
